const prefixUrl = 'http://localhost:8000'; // inside .env variable normaly
const taskApi = {
  create: params => axios.post(prefixUrl + '/tasks', params),
  read: id => axios.get(prefixUrl + `/tasks/${id}`),
  update: (id, params) => axios.put(prefixUrl + `/tasks/${id}`, params),
  delete: id => axios.delete(prefixUrl + `/tasks/${id}`),
  list: params => axios.get(prefixUrl + `/tasks`, params)
};
export default taskApi;
