import React, { useState } from 'react';
import taskApi from '../connexion/tasks';

export type TaskState = {
    title: string;
    description:string;
    echeance:string|null,
    status:string|null

}
enum Slug {
    TITLE = 'title', DESCRIPTION = 'description',DATE = 'echeance', STATUS='status'
}
const AddTask = (props:any) =>{

    const [state,setState] = useState<TaskState>({
        title:'',
        description:'',
        echeance: null,
        status: null,
    })
    const handleOnchage = (value:string, slug: Slug) => {
        setState((current:any) => ({...current,[slug] :value}))
    }

    const handleCreate =() =>
    {
        taskApi.create(state)
            .then(() =>setState({
                title:'',
                description:'',
                echeance: null,
                status: null,

            })).catch((erro:any) => console.error(erro));
    }
    const currentDate = new Date().toISOString().split('T')[0];
    return (
        <>
            <button type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#staticBackdrop">
            Add Tasks
            </button>

            <div className="modal fade" id="staticBackdrop" data-bs-backdrop="static" data-bs-keyboard="false" tabIndex={-1} aria-labelledby="staticBackdropLabel" aria-hidden="true">
            <div className="modal-dialog modal-dialog-centered">
                <div className="modal-content">
                <div className="modal-header">
                    <h1 className="modal-title fs-5" id="staticBackdropLabel">New Task</h1>
                    <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                </div>
                <div className="modal-body">
                    <div className="mb-3">
                        <label htmlFor="exampleFormControlInput1" className="form-label">
                            Title
                        </label>
                        <input
                            type="text"
                            value={state.title}
                            className="form-control"
                            id="exampleFormControlInput1"
                            placeholder="Title"
                            onChange={(e:any) => handleOnchage(e.target.value,Slug.TITLE)}
                        />
                    </div>
                    <div>
                        <label htmlFor="exampleFormControlInput1" className="form-label">
                            Description
                        </label>
                        <textarea
                            className="form-control"
                            id="exampleFormControlInput1"
                            placeholder="Description"
                            value={state.description}
                            onChange={(e:any) => handleOnchage(e.target.value,Slug.DESCRIPTION)}
                        />
                    </div>

                    <div className="mb-3">
                        <label htmlFor="exampleFormControlInput1" className="form-label">
                            Title
                        </label>
                        <input
                            type="date"
                            className="form-control"
                            id="exampleFormControlInput1"
                            placeholder="EchÃ©ance"
                            min={currentDate}
                            value={state.echeance ?? ''}
                            onChange={(e:any) => handleOnchage(e.target.value,Slug.DATE)}
                        />
                    </div>
                </div>
                <div className="modal-footer">
                    <button onClick={() => handleCreate()} type="button" className="btn btn-secondary" data-bs-dismiss="modal">Create</button>
                    <button type="button" className="btn btn-primary">Cancel</button>
                </div>
                </div>
            </div>
            </div>
        </>
    );
}

export default AddTask
